mapper.select:
select js.id, js.is_type isType, js.bui_name buiName, js.consignment_price consignmentPrice, js.server_charge serverCharge, js.consignment_status consignmentStatus, js.to_pay toPay, cuc.collection_hash collectionHash, cs.nick_name collectorName, cs.phone collectorPhone, mr.nick_name sellerName, mr.phone sellerPhone, o.pay_type payType, o.order_amount orderAmount, o.created_time createOrderTime, o.pay_time payTime from cnt_consignment js left join cnt_user cs on js.send_user_id=cs.id left join cnt_user mr on js.pay_user_id=mr.id left join cnt_order o on js.order_id=o.id left join cnt_user_collection cuc on js.bui_id=cuc.id
and js.is_type=0 and js.id = #{id} and js.bui_name like concat('%', #{buiName}, '%') and js.consignment_status = #{consignmentStatus} and cs.nick_name like concat('%', #{collectorName}, '%') and cs.phone = #{collectorPhone} and mr.nick_name like concat('%', #{sellerName}, '%') and mr.phone = #{sellerPhone} and o.pay_type = #{payType} and js.to_pay = #{toPay}
order by js.created_time desc

select js.id, js.is_type isType, js.bui_name buiName, js.consignment_price consignmentPrice, js.server_charge serverCharge, js.consignment_status consignmentStatus, js.to_pay toPay, cs.nick_name collectorName, cs.phone collectorPhone, mr.nick_name sellerName, mr.phone sellerPhone, o.pay_type payType, o.order_amount orderAmount, o.created_time createOrderTime, o.pay_time payTime from cnt_consignment js left join cnt_user cs on js.send_user_id=cs.id left join cnt_user mr on js.pay_user_id=mr.id left join cnt_order o on js.order_id=o.id
and js.is_type=1 and js.id = #{id} and js.bui_name like concat('%', #{buiName}, '%') and js.consignment_status = #{consignmentStatus} and cs.nick_name like concat('%', #{collectorName}, '%') and cs.phone = #{collectorPhone} and mr.nick_name like concat('%', #{sellerName}, '%') and mr.phone = #{sellerPhone} and o.pay_type = #{payType} and js.to_pay = #{toPay}
order by js.created_time desc
